import React from 'react'
import Title from "../components/reusables/Title";
import Tweet from "../components/Tweet";
import styled from "styled-components";
import dp from '../dp.jpg';

export default function Profile() {

    return (
        <ProfileWrapper className="home col-sm-10 col-md-10 col-lg-6">
            <Title title="oluwakayy" username='@oluwakayy' icon="null" />
            <div className="parent">
                <div className="top-image">
                    <img
                        className="img-fluid img-bd"
                        src={dp}
                        alt="backdrop poster"
                    />
                </div>

                <div className="bg">
                        <div className="row no-gutters">
                            <div className="col-sm-5 col-lg-2 ">
                                <img
                                    src={dp}
                                    className="card-img img-fluid"
                                    alt="poster"
                                />
                            </div>
                            <div className="col-sm-6 col-lg-4 card-body pt-0 ml-3">
                                <h4 className="card-title ">{'title'}</h4>
                                <p className="card-text">{'overview'}</p>
                                <button type="button" className="btn mr-2 mt-2">
                                    PG-13
                                </button>
                                <span className="badge badge-info mr-2">Adventure</span>
                                <span className="badge badge-danger mr-2">Drama</span>
                                <span className="badge badge-success">Thriller</span>
                                <p className="card-text mt-3">
                                    <small className="mr-3">{'date'}</small>
                                    <span className="badge btn stars-no">
                                        {'ratings.toString()'}
                                    </span>
                                </p>
                            </div>

                            <div className="col-lg-5 ml-3">
                                <p className="card-title">Cast</p>
                                <div className="cast row">

                                </div>
                            </div>
                        </div>

                    <div className="row card">
                        <h3 className="card-title">Trailers</h3>
                        <div className="trailer-div">

                        </div>
                    </div>
                </div>
            </div>

            {/* <Tweet tweet={tweet} name='nurse kay' username='@oluwakayy'/>  */}
        </ProfileWrapper>
    )
}


const ProfileWrapper = styled.div`
.top-image, .img-bd {
    position:relative;
    width:100%;
    top:5px;
    z-index:-1;
    max-height:40vh
  }

.card-title{
    text-shadow: 2px 2px rgba(0,0,0,0.1);
    font-weight: 700;
    color:var(--mainRed)
  }

  .card-img {
    border: 6px solid rgba(0,0,0,0.1);
    border-radius: 10px;
  }
.bg {
    background-color: transparent;
    position: absolute;
    width: 94%;
    left: 3rem;
    top: 15rem;
    z-index: 1;
  }



  .fa-heart,
  .fa-trash {
    color: var(--mainRed) !important;
    transition: all 0.25s ease-in-out;
  }
  .fa-heart:hover,
  .fa-trash:hover {
    font-size: 1.35rem;
  }
`;
